import { useEffect } from "react";

const RandomImagesContainer = () => {
  useEffect(() => {
    document.addEventListener("DOMContentLoaded", () => {
      const imageContainer = document.getElementById("imageContainer");
      const imageList = [
        "../assets/Random Images/Image1.HEIC",
        "../assets/Random Images/Image2.Jpg",
        "../assets/Random Images/Image 3.Jpg",
        "../assets/Random Images/Image 4.Jpg",
        "../assets/Random Images/Image 5.Jpg",
      ];
      const createRandomImage = () => {
        const selectedImage =
          imageList[Math.floor(Math.random() * imageList.length)];
        const newImg = document.createElement("img");
        newImg.setAttribute("src", selectedImage);
        // newImg.classList.add("random-image"); //(reminder)adding the "random-image" class to my newImg variable

        // const containerWidth = imageContainer.offsetWidth;
        // const containerHeight = imageContainer.offsetHeight;
        const imgWidth = 50;
        const imgHeight = 50;

        const randomLeft = Math.floor(
          Math.random() * (imgWidth)
        );
        const randomTop = Math.floor(
          Math.random() * (imgHeight)
        );

        newImg.style.left = `${randomLeft}px`;
        newImg.style.top = `${randomTop}px`;
        newImg.style.position = "absolute";

        imageContainer.appendChild(newImg);
      };
    });
  });
};

